# Function to convert Channel to m3u line
def to_m3u_line(channel):
    return f'#EXTINF:-1 tvg-name="{channel.name}" tvg-logo="{channel.logo}",{channel.name}\n{channel.url}'

# Write the playlist.m3u8 file
playlist_path = "playlist.m3u8"
with open(playlist_path, "w", encoding="utf-8") as playlist:
    playlist.write("#EXTM3U\n")
    for channel in channels:
        playlist.write(to_m3u_line(channel) + "\n")

print(f"Playlist generated and saved as {playlist_path}")
